var umur = 18;
console.log("umur saya: ", umur);
var islife = true;
console.log("apakah hidup: ", islife);
let x = null;
console.log("nilai_x", x);
// non-primitif
// tipedata object
var person = { nama: "Arlot", umur: 10 };
